import React from "react"
import Button from "../Buttons/Button"

export default function Post({onChangeView,postData,postArr }) {
    let style = {
        wrapper: {
            width: "900px",
            height: "600px",
            border: "7px dashed black",
            position: "relative",
            marginLeft: "50%",
            marginTop: "20%"
        },
        pTag : {
            color:"green"
        }
    }
    return (
        <div style={{ position: "absolute" }}>
            <div style={style.wrapper}>
                <div>
                    <p style={style.pTag}><h1>Title</h1></p>
                    <p><h2>{postData.title}</h2></p>

                    <p style={style.pTag}><h1>User Name Of Creator</h1></p>
                    <p><h2>{postData.username}</h2></p>

                    <p style={style.pTag}><h1>Comment</h1></p>
                    {postArr?.length ? postArr.map((item,i)=>{
                        return <p><h3></h3></p>
                    }) : "No Comments Found!!"}
                </div>
            </div>
            <Button onChangeView={onChangeView} />
        </div>
    )
}
