import React from "react"
import Button from "../Buttons/Button"

export default function Post({onChangeView,postData,postArr }) {
    let style = {
        wrapper: {
            width: "auto",
            height: "auto",
            border: "7px dashed black",
            position: "relative",
            marginLeft: "50%",
            marginTop: "20%"
        },
    }
    console.log(postArr,'postArr')
    return (
        <div style={{ position: "absolute" }}>
            <div style={style.wrapper}>
                <div>
                    <p style={{color:"green"}}><h3>Title: {postData.title}</h3></p>
                    {/* <p><h2></h2></p> */}

                    <p style={{color:"orange"}}><h3>User Name Of Creator: {postData.username}</h3></p>
                    {/* <p><h2>{postData.username}</h2></p> */}
                    <div>
                    <p style={{color:"blue"}}><h3>Comments</h3></p>
                    {postArr?.length ? postArr.map((item,i)=>{
                        return <><p style={{color:"purple"}}><h3>Subject: {item.name}</h3></p>
                        <p style={{color:"grey"}}><h3>Email : {item.email}</h3></p>
                        <p style={{color:"gray"}}><h3>Body : {item.body}</h3></p></>
                    }) : <p><h2>No Comments Found!!</h2></p>}
                    </div>
                </div>
            </div>
            <Button onChangeView={onChangeView} />
        </div>
    )
}
