import React, { useState, useEffect } from "react";
import get from "../config/communication"
import Table from "./Table/Table";
import User from "./Users/User";
import Search from "./Search/Search"


export default function MainComponent() {
    let initialState = {
        posts: [],
        comments: [],
        users: [],
        newArr: [],
        view: "allPost",
        individualUser: {}
    }
    const [state, setState] = useState(initialState)
    useEffect(() => {
        storeAllData()
    }, [])
    const storeAllData = async () => {
        let posts = await get("posts")
        let users = await get("users")
        let comments = await get("comments")
        setState({ ...state, posts, users, comments })
    }
    useEffect(() => {
        expensiveComputation()
    }, [state.posts])
    const expensiveComputation = () => {
        console.log(state, 'statee')
        let newArr = []
        state.posts.map((el) => {
            state.users.find((elem) => {
                if (elem.id == el.userId) {
                    newArr.push({ title: el.title, username: elem.username, id: elem.id })
                }
            })
        })
        setState({ ...state, newArr })
    }

    const onChangeView = (e, i) => {
        e.preventDefault()
        if (state.view == "allPost") {
            let user = state.users.find((el) => {
                console.log(el.id, 'ellelelele', i)
                if (el.id == i) {
                    return el
                }
            })
            setState({ ...state, view: "users", individualUser: [user] })
        } else if(state.view == "users"){
            setState({ ...state, view: "allPost" })
        }
    }

    const onSearch = (e) =>{
        let value = e.target.value
        if(state.newArr.length){
            let filteredArr = state.newArr.filter((el)=>{
                if(el.username.slice(0,e.target.value.length) == e.target.value) {
                    return el
                }
            })
        }
    }

    // console.log(state, 'posts')

    return (
        <div>
            {state.view == "allPost" ? <><div><h1>All Posts</h1></div>
            <Search onSearch={onSearch}/>
                <Table newArr={state.newArr} headings={["Title", "Username"]} onChangeView={onChangeView} view={state.view} /></> : <User individualUser={state.individualUser} newArr={state.individualUser} view={state.view} onChangeView={onChangeView} />}
        </div>
    )
}
