import React, { useState, useEffect } from "react";
import get from "../config/communication"
import Home from "./HomePage/Home"
import User from "./Users/User";
import Post from "./Posts/Post"

export default function MainComponent() {
    let initialState = {
        posts: [],
        comments: [],
        users: [],
        newArr: [],
        view: "allPost",
        individualUser: {},
        replicaArr: [],
        postArr: []
    }
    const [state, setState] = useState(initialState)
    useEffect(() => {
        storeAllData()
    }, [])
    const storeAllData = async () => {
        let posts = await get("posts")
        let users = await get("users")
        let comments = await get("comments")
        setState({ ...state, posts, users, comments })
    }
    useEffect(() => {
        expensiveComputation()
    }, [state.posts])
    const expensiveComputation = () => {
        console.log(state, 'statee')
        let newArr = []
        state.posts.map((el) => {
            state.users.find((elem) => {
                if (elem.id == el.userId) {
                    newArr.push({ title: el.title, username: elem.username, id: elem.id })
                }
            })
        })
        setState({ ...state, newArr, replicaArr: newArr })
    }

    const onChangeView = (e, i, name) => {
        e.preventDefault()
        if(state.view == "allPost"){
            if (name =="username") {
                let user = state.users.find((el) => {
                    console.log(el.id, 'ellelelele', i)
                    if (el.id == i) {
                        return el
                    }
                })
                setState({ ...state, view: "users", individualUser: [user] })
            }
            else if(name == "post") {
                let postData = state.newArr.find((el) => {
                    if(el.postId == i){
                        return el
                    }
                })
                let post = []
                state.comments.find((el) => {
                    if(el.postId == i){
                        post = [...post,el] 
                    }
                })
                console.log(post,'post')
                setState({ ...state, view: "Post" ,postArr : post})
            }
        }
          else if (state.view == "users") {
            setState({ ...state, view: "allPost" })
        }
    }

    const onSearch = (e) => {
        let value = e.target.value
        if (value && state.newArr.length) {
            let filteredArr = state.newArr.filter((el) => {
                if (el.username.toLowerCase().includes(value.toLowerCase())) {
                    return el
                }
            })
            setState({ ...state, newArr: filteredArr })
        } else if (value == "") {
            setState({ ...state, newArr: state.replicaArr })
        }
    }

    // console.log(state, 'posts')
    const props = {
        newArr: state.newArr,
        onChangeView: onChangeView,
        view: state.view
    }
    return (
        <div>
            {state.view == "allPost" ? <Home {...props} onSearch={onSearch} /> : state.view == "Post" ? <Post {...props} postArr={state.postArr}/> : <User individualUser={state.individualUser} {...props} />}
        </div>
    )
}
